import React, { useState } from "react";
import savedCard from "../../images/iconGuardar.svg";
import imagePerrito from "../../images/perrito.jpg";
import imageLago from "../../images/lago.jpg";
import imageParque from "../../images/parque.jpg";

function NewsCard() {
  const [showLoginMessage, setShowLoginMessage] = useState(false);

  // Sample data for the cards
  const cardData = [
    {
      _id: "1",
      link: imagePerrito,
      title: "Todo el mundo necesita un lugar de reflexión en la naturaleza",
      date: "4 de noviembre de 2020",
      description: "Desde que leí el influyente libro de Richard Louv, El último niño en el bosque, la idea de tener un lugar de reflexión especial para mi se me ha quedado grabada. Este consejo, que...",
    },
    {
      _id: "2",
      link: imageLago,
      title: "La naturaleza te hace mejor",
      date: "19 de febrero de 2019",
      description: "Milenios atrás ya nos percatamos de ello: el sonido del océano, los aromas de un bosque, la forma en que la luz del sol moteada baila entre las hojas.",
    },
    {
      _id: "3",
      link: imageParque,
      title: "La naturaleza te hace mejor",
      date: "19 de febrero de 2019",
      description: "Milenios atrás ya nos percatamos de ello: el sonido del océano, los aromas de un bosque, la forma en que la luz del sol moteada baila entre las hojas.",
    },
  ];

  // function handleSavedCard() {
  //   // Implement your logic for saving the card here
  //   // For this example, let's just log the card data
  //   console.log("Card saved:", cardData);
  // }



  function hideLoginMessage() {
    setShowLoginMessage(true);
  }

  return (
    <div  className="cards">
      {cardData.map((card) => (
        <div className="card" key={card._id}>
          <div className="card__header">
            <img
              className="card__icon-saved"
              src={savedCard}
              alt={`Icono para agregar/quitar de favoritos`}
              onClick={() => {
                hideLoginMessage();
              }}
            />
            {showLoginMessage && (
              <p className="card__login-message">Inicia sesión para guardar artículos</p>
            )}
          </div>
          <img
            className="card__image"
            src={card.link}
            alt={card.title}
            style={{ backgroundImage: `url(${card.link})` }}
          />
          <div className="card__footer-image">
            <h4 className="card__date">{card.date}</h4>
            <h3 className="card__title">{card.title}</h3>
            <h3 className="card__description">{card.description}</h3>
          </div>
        </div>
      ))}
    </div>
  );
}

export default NewsCard;
