    return (
      <div className="body">
        <div className="page">
          <CurrentUserContext.Provider value={currentUser}>
             <Header loginPopUp={handleLoginPopUp}  loggedIn={isLoggedIn} onLogout={handleLogout} />
            <Routes>
              <Route
                path="/signin"
                element={
                  <Login onLoggedIn={handleLogin} loggedIn={isLoggedIn} />
                }
              />
              <Route
                path="/signup"
                element={
                  <Register
                    onRegister={handleRegisterUser}
                    loggedIn={isLoggedIn}
                  />
                }
              />
              <Route path="/" element={<Main />} />
            </Routes>

            <Footer />
          </CurrentUserContext.Provider>
        </div>
      </div>
    );
  }

export default App;
