import React from "react";
import SavedNewsHeader from "../SavedNewsHeader/SavedNewsHeader";
import NewsCardList from "../NewsCardList/NewsCardList";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";

function SavedNews(props) {
  const { cards = [], isLoggedIn, onDeleteCard, searchQueries } = props;
  
  console.log("SavedNews component rendered");
  console.log("Is logged in:", props.isLoggedIn);
  console.log("Saved cards:", props.cards);

  const savedCardsCount = Array.isArray(props.cards) ?props.cards.length : 0;

  return (
    <>
      {props.isLoggedIn ? (
        <section>
          <Header isLoggedIn={props.isLoggedIn} />
          <SavedNewsHeader
            savedCardsCount={savedCardsCount}
            searchQueries={props.searchQueries}
          />
          <div className="saved-news">
            {props.cards.length > 0 ? (
              // Mostrar tarjetas si hay alguna
              cards.map((card, index) => (
                <NewsCardList
                  key={index}
                  card={card}
                  onDelete={() => onDeleteCard(index)}
                />
              ))
            ) : (
              // Mostrar mensaje si no hay tarjetas
              <p>No hay tarjetas guardadas.</p>
            )}
          </div>
          <Footer />
        </section>
      ) : (
        ""
      )}
    </>
  );
}

export default SavedNews;
